import { GoBackLink } from "pages/MovieDetails/MovieDetailsGoBackLink/MovieDetailsGoBackLink";
import { useLocation, useParams } from "react-router-dom";
import { useGetPersonDetailsQuery } from "store/movies/getMoviesRTK";
import {
  PersonDescr,
  PersonSubTitle,
  PersonTitle,
  PersonWrapper,
  StyledListDescr,
} from "./PersonDetails.styled";

const PersonDetails = () => {
  const { personId } = useParams();
  const personDetailsLocation = useLocation();
  const backLinkHref = personDetailsLocation.state?.from ?? "/movies";
  const { data } = useGetPersonDetailsQuery(personId);
  return (
    <>
      {" "}
      <GoBackLink backLinkHref={backLinkHref} />
      {data && (
        <PersonWrapper>
          {/* <PersonImage movie={movie} /> */}
          <PersonImage image={data.profile_path} />

          <div>
            <PersonTitle>{movie.original_title}</PersonTitle>
            <PersonSubTitle>
              Rating: {Math.round(movie.vote_average)}
            </PersonSubTitle>
            <PersonSubTitle>Overview</PersonSubTitle>
            <PersonDescr>{movie.overview}</PersonDescr>
            <PersonSubTitle>Genres</PersonSubTitle>
            <StyledListDescr>
              {movie.genres?.map((genre) => (
                <li key={genre.id}>{genre.name}</li>
              ))}
            </StyledListDescr>
          </div>
        </PersonWrapper>
      )}
    </>
  );
};

export default PersonDetails;
