import styled from "@emotion/styled";
export const PersonCardWrapper = styled.div`
  display: flex;
  gap: 12px;
  margin-top: 12px;
  margin-bottom: 12px;
  background-color: rgba(0, 0, 0, 0.7);
  padding: 20px;
  border-radius: 10px;
`;

export const PersonCardTitle = styled.h2`
  font-size: 26px;
  margin-bottom: 12px;
`;

export const PersonCardSubTitle = styled.h3`
  font-size: 26px;
  margin-bottom: 6px;
`;

export const PersonCardDescr = styled.p`
  font-size: 24px;
  color: #888888;
  margin-bottom: 12px;
`;
