import styled from "@emotion/styled";
export const PersonWrapper = styled.div`
  display: flex;
  gap: 12px;
  margin-top: 12px;
  margin-bottom: 12px;
  background-color: rgba(0, 0, 0, 0.7);
  padding: 20px;
  border-radius: 10px;
`;

export const PersonTitle = styled.h2`
  font-size: 26px;
  margin-bottom: 12px;
`;

export const PersonSubTitle = styled.h3`
  font-size: 26px;
  margin-bottom: 6px;
`;

export const PersonDescr = styled.p`
  font-size: 24px;
  color: #888888;
  margin-bottom: 12px;
`;

export const StyledListDescr = styled.ul`
  font-size: 22px;
  display: flex;
  gap: 18px;
  padding: 0;
  border-radius: 20px;
  color: #888888;
  margin-bottom: 12px;
`;
