import styled from '@emotion/styled';

export const ReviewsStyledList = styled.ul`
  margin-top: 12px;
`;
export const ReviewsListItem = styled.li`
  font-size: 14px;
  padding: 6px;
  background-color: #2a2a2a;
  border-radius: 14px;

  :not(:last-child) {
    margin-bottom: 12px;
  }
`;
export const ReviewsDescription = styled.p`
  color: #888888;
  margin-top: 6px;
`;
export const ReviewsListText = styled.p``;
export const ReviewsListSpan = styled.span`
  color: #ffc700;
`;
