  //       setMovie(movieById);
  //     } catch (error) {
  //       console.log(error);
  //     }
  //   };
  //   fetchMovieById();
  // }, [movieId]);

  return (
    <>
      <GoBackLink backLinkHref={backLinkHref} />
      <MovieDetailsCard movie={movie}></MovieDetailsCard>

      <AdditionalInfo></AdditionalInfo>
    </>
  );
};

export default MovieDetails;
