import { useState } from "react";
import { CardImageImg, CardImageWrapper } from "./DetailsCardImage.styled";
import { Loader } from "components/Loader/Loader";
import { BASE_POSTER_URL, PLACEHOLDER } from "utils/constants";

export const DetailsCardImage = ({ data }) => {
  const [isLoading, setIsLoading] = useState(true);
  const handleImageLoad = () => {
    setIsLoading(false);
  };
  return (
    <CardImageWrapper>
      {isLoading && <Loader />}
      <CardImageImg
        src={`${
          data.poster_path
            ? BASE_POSTER_URL + data.poster_path
            : PLACEHOLDER + "?text=" + data.original_title
        }`}
        alt="get"
        onLoad={handleImageLoad}
        style={{ display: isLoading ? "none" : "block" }}
      />
    </CardImageWrapper>
  );
};
